  nombre_indicador TEXT,
  variables_indicador TEXT,
  unidad_medida VARCHAR(255),
  tipos_operacion VARCHAR(255),
  programacion_inicial VARCHAR(255),
  avance VARCHAR(255),
  programacion_modificada VARCHAR(255),
  calendario_trimestral TEXT,
  justificacion_impacto TEXT,
  id_dependencia INT
)ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;


DROP TABLE IF EXISTS unidades_medida; 
CREATE TABLE unidades_medida(
  id_unidad INT NOT NULL PRIMARY KEY AUTO_INCREMENT,
  nombre_unidad VARCHAR(255) NOT NULL,
  descripcion_unidad TEXT,
  id_registro INT,
  last_edit INT,
  fecha_alta DATETIME DEFAULT CURRENT_TIMESTAMP()
)ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;


DROP TABLE IF EXISTS unidades_medida_eliminadas; 
CREATE TABLE unidades_medida_eliminadas(
  id_unidad_eliminada INT NOT NULL PRIMARY KEY AUTO_INCREMENT,
  nombre_unidad VARCHAR(255) NOT NULL,
  descripcion_unidad TEXT,
