INSERT INTO programas_presupuestarios (codigo_programa, nombre_programa, objetivo_pp, dependencia_general_gaceta, pilar_o_eje, tema_desarrollo, anio) VALUES 
("01020401","Derechos Humanos","Engloba los proyectos orientados a proteger, defender y garantizar los derechos humanos de todas las personas que se encuentren en cada territorio municipal, sin discriminación por condición alguna y fomentar la cultura de los derechos humanos para promover el respeto y la tolerancia entre los individuos en todos los ámbitos de la interrelación social apoyando a las organizaciones sociales que impulsan estas actividades.","A02 Derechos Humanos","Pilar 4 Seguridad","Derechos Humanos","2023"),
("01030101","Conducción de las políticas generales de gobierno.","Incluye las acciones que favorezcan el desarrollo de un gobierno democrático que impulse la participación social y ofrezca servicio de calidad en el marco de legalidad y justicia, para elevar las condiciones de vida de la población.","J00 Gobierno municipal.","Eje transversal II Gobierno Moderno, Capaz y Responsable","Comunicación y diálogo con la ciudadanía como elemento clave de gobernabilidad.","2023"),
("01030201","Democracia y pluralidad política.","Incorpora las acciones orientadas a la realización de acciones de apoyo al estado democrático con la participación ciudadana y la consolidación del estado de derecho y la justicia social, propiciando una cultura política y fortaleciendo sistema de partidos.","J00 Gobierno municipal.","Eje transversal II Gobierno Moderno, Capaz y Responsable","Comunicación y diálogo con la ciudadanía como elemento clave de gobernabilidad.","2023"),
("01030301","Conservación del patrimonio público","Incluye las acciones encaminadas a mantener y transmitir el conocimiento del patrimonio público tangible e intangible, como devenir de la identidad de los mexiquenses.","J00 Gobierno Municipal","Pilar 3 Territorial","Ciudades y comunidades sostenibles","2023"),
("01030401","Desarrollo de la función pública y ética en el servicio público","Considera las acciones orientadas a la mejora en la prestación de los servicios que recibe la población de manera clara, honesta, pronta y expedita, promoviendo que los servidores públicos realicen su función con calidez, y cuenten con las competencias y conducta ética necesarias en el servicio público, conforme a los principios que rigen la actuación del servidor público.","K00 Contraloría","Eje transversal II Gobierno Moderno, Capaz y Responsable","Eficiencia y eficacia en el sector público","2023"),
("01030402","Sistema Anticorrupción del Estado de México y Municipios","Conjunto de acciones orientadas a establecer las bases de coordinación entre el Estado y los Municipios para el funcionamiento de los Sistemas Anticorrupción, de conformidad con lo dispuesto en la Constitución Política de los Estados Unidos Mexicanos, la Constitución Política del Estado Libre y Soberano de México, la Ley General del Sistema Nacional Anticorrupción y la Ley del Sistema Anticorrupción del Estado de México y Municipios, para que las autoridades estatales y municipales competentes prevengan, investiguen y sancionen las faltas administrativas y los hechos de corrupción.","","Eje transversal II: Gobierno Moderno, Capaz y Responsable","Sistema Anticorrupción del Estado de México y Municipios","2023"),
("01030501","Asistencia Jurídica al Ejecutivo.","Comprende todas las acciones orientadas al fortalecimiento y mejora de los procedimientos regulatorios y conductos legales establecidos, que influyan directamente en la garantía jurídica del gobierno y la sociedad.","M00 Consejería Jurídica.","Eje transversal II Gobierno Moderno, Capaz y Responsable","Eficiencia y eficacia en el sector público","2023"),
("01030801","Política territorial.","Es el conjunto de acciones a aplicar en una demarcación territorial definida, en beneficio de toda la población o comunidades específicas ahí establecidas.","J00 Gobierno municipal.","Pilar 3 Territorial","Ciudades y comunidades sostenibles","2023"),
("01030902","Reglamentación Municipal","Se orienta al cumplimiento de las atribuciones contenidas en el Título VI de la Ley Orgánica Municipal del Estado de México, vigente.","D00 Secretaría del Ayuntamiento","Eje transversal II Gobierno Moderno, Capaz y Responsable","Estructura del gobierno municipal","2023"),
("01030903","Mediación y conciliación municipal","Se orienta al cumplimiento de las atribuciones contenidas en el Título V de la Ley Orgánica Municipal del Estado de México, vigente.","M00 Consejería jurídica.","Pilar 4 Seguridad","Mediación y conciliación","2023"),
("01030904","Coordinación intergubernamental regional.","Se orienta al impulso del desarrollo armónico sustentable de las regiones con la eficaz intervención y coordinación de un ayuntamiento con otros gobiernos municipales, estatales y el gobierno federal en beneficio de la población y sus actividades productivas; así mismo, incluye las acciones que permitan fortalecer la cooperación regional, incluyendo el desarrollo metropolitano, para alcanzar una efectiva coordinación y aplicación de políticas públicas de ámbito regional.","N00 Dirección de Desarrollo Económico","Eje transversal II Gobierno Moderno, Capaz y Responsable","Coordinación Institucional","2023"),
("01040101","Relaciones Exteriores","Incluye todas las acciones relacionadas con la celebración de reuniones, eventos, convenios y acuerdos para la formalización de proyectos de cooperación internacional y para la promoción, económica, comercial y turística de los municipios. Considera también todas las actividades de coordinación, gestión y enlace para la prestación de servicios de protección y apoyo a la población que viven en el extranjero y a sus familias en las comunidades de origen.","A00 Presidencia","Pilar 1 Social","Desarrollo humano incluyente, sin discriminación y libre de violencia","2023"),
("01050201","Impulso al federalismo y desarrollo municipal","Considera acciones que permitan generar una relación respetuosa, solidaria y equitativa con la federación y el Estado mediante la descentralización de facultades, funciones y recursos, estableciendo esquemas de coordinación, que equilibre las cargas de responsabilidad y beneficios en las acciones compartidas, además de fomentar planes, programas y políticas de desarrollo municipal de largo plazo.","J00 Gobierno Municipal","Eje Transversal III Tecnología y Coordinación para el Buen Gobierno","Alianzas para el desarrollo","2023"),
("01050202","Fortalecimiento de los Ingresos","Incluye acciones que permitan elevar la calidad, capacidad y equidad tributaria, con seguridad jurídica, transparencia y simplificación de trámites para el contribuyente, desarrollando un régimen fiscal que amplíe la base de contribuyentes e intensificando las acciones de control para el cumplimiento de las obligaciones tributarias que eviten la elusión y evasión fiscal.","L00 Tesorería","Eje transversal II Gobierno Moderno, Capaz y Responsable","Finanzas públicas sanas","2023"),
("01050203","Gasto Social e Inversión pública","Elaborar con las Dependencias y Organismos municipales los planes y programas estatales, sectoriales, regionales y los referentes a inversión pública física, vigilando que los recursos que se asignen se apliquen de acuerdo con la normatividad vigente, así como fortalecer la relación con el estado, la federación y otros municipios, reconociendo sus responsabilidades en la ejecución de la obra pública.","F00 Desarrollo Urbano y Obras Públicas","Eje transversal II Gobierno Moderno, Capaz y Responsable","Finanzas públicas sanas","2023"),
("01050204","Financiamiento de la Infraestructura para el desarrollo","Fomentar el desarrollo económico y la inversión productiva en los sectores económicos, involucrando al sector privado en esquemas de financiamiento para desarrollar infraestructura, ampliar y facilitar medios de financiamiento a los municipios, asegurando que la aplicación de los recursos promueva proyectos estratégicos.","F00 Desarrollo Urbano y Obras Públicas","Eje transversal II Gobierno Moderno, Capaz y Responsable","Finanzas públicas sanas","2023"),
("01050205","Planeación y presupuesto basado en resultados","Incluye las acciones y procedimientos necesarios para desarrollar y fortalecer las fases para la planeación, programación, presupuestación, seguimiento y evaluación programático – presupuestal, considerando las fases del registro contable – presupuestal y el correspondiente proceso de rendición de cuentas, adicionalmente, incluye los procedimientos de planeación y evaluación de los planes de desarrollo municipal, los programas regionales y sectoriales que de él deriven.","E01 Planeación - Información, Planeación, Programación y Evaluación","Eje transversal II Gobierno Moderno, Capaz y Responsable","Gestión para Resultados y evaluación del desempeño","2023"),
("01050206","Consolidación de la administración pública para resultados.","Comprende el conjunto de actividades y herramientas para coadyuvar a que la actuación de los servidores públicos sea eficaz, eficiente y transparente, a fin de generar resultados con apego a los principios de legalidad, objetividad, profesionalismo, honradez, lealtad, imparcialidad, eficiencia, eficacia, equidad, transparencia, economía, integridad, que permiten la toma de decisiones sobre la aplicación de los recursos públicos con el objeto de mejorar la calidad del gasto público y la rendición de cuentas.","J00 Gobierno municipal.","Eje transversal II Gobierno Moderno, Capaz y Responsable","Gestión para Resultados y evaluación del desempeño","2023"),
("01070101","Seguridad pública.","Incluye los proyectos orientados a combatir la inseguridad pública con estricto apego a la ley para erradicar la impunidad y la corrupción, mediante la profesionalización de los cuerpos de seguridad, modificando los métodos y programas de estudio para humanizarlos, dignificarlos y hacerlos más eficientes, aplicando sistemas de reclutamiento y selección confiable y riguroso proceso estandarizado de evaluación, así como promover la participación social en acciones preventivas del delito.","Q00 Seguridad Pública y tránsito.","Pilar 4: Seguridad","Seguridad con visión ciudadana","2023"),
("01070201","Protección civil.","Engloba los proyectos que integran acciones dirigidas a la protección de la vida e integridad física de las personas, a través de la capacitación y organización de la sociedad, para evitar y reducir los daños por accidentes, siniestros, desastres y catástrofes, y fomentar la cultura de autoprotección, prevención y solidaridad en las tareas de auxilio y recuperación entre la población, así como proteger la infraestructura urbana básica y el medio ambiente.","Q00 Seguridad Pública y tránsito.","Pilar 3: Territorial","Riesgo y protección civil","2023"),
("01070401","Coordinación intergubernamental para la seguridad pública.","Se orienta a la coordinación de acciones municipales que permitan eficientar los mecanismos en materia de seguridad pública con apego a la legalidad que garantice el logro de objetivos gubernamentales.","Q00 Seguridad pública y tránsito.","Pilar 4: Seguridad","Seguridad con visión ciudadana","2023"),
("01080101","Protección jurídica de las personas y sus bienes.","Conjunto de acciones para el fortalecimiento de la certeza jurídica, edificando una alianza entre los distintos órdenes de gobierno y la población, a fin de consolidar una cultura de legalidad que impacte en la prevención del delito.","J00 Gobierno municipal.","Pilar 4: Seguridad.","Seguridad con visión ciudadana","2023"),
("01080102","Modernización del catastro mexiquense.","Engloba las acciones que se llevan a cabo por los gobiernos municipales en los procesos de registro de bienes inmuebles en el territorio estatal, así como determinar extensión geográfica y valor catastral por demarcación que definan la imposición fiscal.","J00 Gobierno municipal.","Eje transversal II Gobierno Moderno, Capaz y Responsable","Finanzas públicas sanas","2023"),
("01080201","Administración del sistema estatal de información estadística y geográfica","Comprende el conjunto de acciones municipales que se llevan a cabo para la captación, registro, procesamiento, actualización y resguardo de información estadística y geográfica del territorio estatal.","E02 Informática","Eje transversal II Gobierno Moderno, Capaz y Responsable","Eficiencia y eficacia en el sector público","2023"),
("01080301","Comunicación pública y fortalecimiento informativo.","Difundir los valores y principios de gobierno, promoviendo la cultura de la información transparente y corresponsable entre gobierno, medios y sectores sociales, con pleno respeto a la libertad de expresión y mantener informada a la sociedad sobre las acciones gubernamentales, convocando su participación en asuntos de interés público.","A01 Comunicación social.","Eje transversal II Gobierno Moderno, Capaz y Responsable","Comunicación y diálogo con la ciudadanía como elemento clave de gobernabilidad","2023"),
("01080401","Transparencia","Se refiere a la obligación que tiene el sector público en el ejercicio de sus atribuciones para generar un ambiente de confianza, seguridad y franqueza, de tal forma que se tenga informada a la ciudadanía sobre las responsabilidades, procedimientos, reglas, normas y demás información que se genera en el sector, en un marco de abierta participación social y escrutinio público; así como garantizar la protección de sus datos personales en posesión de los sujetos obligados.","P00 Atención Ciudadana","Eje transversal II Gobierno Moderno, Capaz y Responsable","Transparencia y rendición de cuentas","2023"),
("01080501","Gobierno Electrónico","Engloba todas las actividades o servicios que las administraciones municipales otorgan a la población a través de tecnologías de información, mejorando la eficiencia y eficacia en los procesos facilitando la operación y distribución de información que se brinda a la población","E02 Informática","Eje transversal III: Conectividad y Tecnología para el Buen Gobierno","Municipio moderno en tecnologías de información y comunicaciones","2023"),
("02010101","Gestión integral de residuos sólidos.","Conjunto articulado e interrelacionado de acciones, para el manejo integral de residuos sólidos, desde su generación hasta la disposición final, a fin de lograr beneficios ambientales, la optimización económica de su manejo y la aceptación social para la separación de los mismos, proporcionando una mejor calidad de vida de la población.","H00 Servicios públicos.","Pilar 3: Territorial","Acción por el clima","2023"),
("02010301","Manejo de aguas residuales, drenaje y alcantarillado","Considera el conjunto de procedimientos que se llevan a cabo para el tratamiento de aguas residuales y saneamiento de redes de drenaje y alcantarillado, manteniendo en condiciones adecuadas la infraestructura para proporcionar una mejor calidad de vida a la población.","","Pilar 3: Territorial","Manejo sustentable y distribución del agua","2023"),
("02010401","Protección al Ambiente","Considera acciones relacionadas con la protección, conservación y restauración del equilibrio ambiental, la mitigación de los contaminantes atmosféricos para mejorar la calidad del aire, así como la gestión integral de los residuos sólidos, el fomento de la participación ciudadana y la promoción de la educación ambiental en todos los sectores de la sociedad, orientadas a promover el desarrollo sustentable en el municipio y el combate al cambio climático en el Estado de México.","G00 Ecología","Pilar 3: Territorial","Vida de los ecosistemas terrestres","2023"),
("02010501","Manejo sustentable y conservación de los ecosistemas y la biodiversidad.","Comprende el conjunto de acciones orientadas al desarrollo de proyectos que contribuyan a la prevención, conservación, protección, saneamiento y restauración de los ecosistemas, con la finalidad de garantizar la permanencia de la biodiversidad en los municipios del Estado de México, así como fomentar la educación ambiental, el manejo de áreas verdes y arbolado en zonas urbanas.","G00 Ecología","Pilar 3: Territorial","Vida de los ecosistemas terrestres","2023"),
("02020101","Desarrollo Urbano","Incluye las acciones para ordenar y regular el crecimiento urbano municipal vinculándolo a un desarrollo regional sustentable, replanteando los mecanismos de planeación urbana y fortaleciendo el papel del municipio en la materia como responsable de su planeación y operación.","F00 Desarrollo Urbano y Obras Públicas","Pilar 3: Territorial","Ciudades y comunidades sostenibles","2023"),
("02020201","Desarrollo Comunitario","Incluye proyectos cuyas acciones de coordinación para la concurrencia de los recursos en los programas de desarrollo social se orientan a la mejora de los distintos ámbitos de los municipios y los grupos sociales que en ellos habitan, en especial a los de mayor vulnerabilidad, y que tengan como propósito asegurar la reducción de la pobreza.","I00 Promoción Social","Pilar 1: Social","Desarrollo humano incluyente, sin discriminación y libre de violencia","2023"),
("02020301","Manejo eficiente y sustentable del agua","Engloba el conjunto de acciones encaminadas al desarrollo de proyectos que propicien en la población el cuidado y manejo eficiente del agua, procurando la conservación del vital líquido para otorgar este servicio con calidad.","","Pilar 3: Territorial","Manejo sustentable y distribución del agua","2023"),
("02020401","Alumbrado público","Contiene el conjunto de acciones encaminadas a otorgar a la población del municipio el servicio de iluminación de las vías, parques y espacios de libre circulación con el propósito de proporcionar una visibilidad adecuada para el desarrollo de las actividades","F00 Desarrollo urbano y obras públicas.","Pilar 3: Territorial","Energía asequible y no contaminante","2023"),
("02020501","Vivienda","Engloba las actividades orientadas a promover y fomentar la adquisición, construcción y mejoramiento de la vivienda en beneficio de la población de menores ingresos, para abatir el rezago existente, y que ésta sea digna y contribuya al mejoramiento de la calidad de vida y al desarrollo e integración social de las comunidades.","I01 Desarrollo Social","Pilar 1: Social","Vivienda digna","2023"),
("02020601","Modernización de los servicios comunales.","Se refiere al conjunto de acciones que se llevan a cabo para la modernización y rehabilitación de plazas, jardines públicos, centros comerciales y demás infraestructura en donde se presten servicios comunales, contando con la participación de los diferentes niveles de gobierno incluyendo la iniciativa privada.","F00 Desarrollo urbano y obras públicas.","Pilar 2: Económico","Infraestructura y modernización de los servicios comunales","2023"),
("02030101","Prevención médica para la comunidad.","Incluye acciones de promoción, prevención y difusión de la salud pública para contribuir a la disminución de enfermedades y mantener un buen estado de salud de la población municipal.","","Pilar 1: Social","Salud y bienestar incluyente","2023"),
("02030201","Atención médica.","Agrupa las líneas de acción dirigidas a proporcionar atención médica a la población mexiquense, con efectividad y calidad de los servicios de salud que otorgan las instituciones del sector público, así como lograr la cobertura universal de los servicios de salud, para reducir los índices de morbilidad y mortalidad aumentando la esperanza de vida de la población de la entidad.","","Pilar 1: Social","Salud y bienestar incluyente","2023"),
("02040101","Cultura física y deporte.","Agrupa los proyectos encaminados a mejorar la estructura jurídica, orgánica, funcional y física; ampliar la oferta y calidad de los servicios que proporcionan las entidades promotoras de actividades físicas, recreativas y deportivas, para fomentar la salud física y mental de la población a través de una práctica sistemática.","","Pilar 1: Social","Cultura física, deporte y recreación","2023"),
("02040201","Cultura y Arte","Incluye los proyectos encaminados a promover la difusión y desarrollo de las diferentes manifestaciones culturales y artísticas.","O00 Educación Cultural y Bienestar Social","Pilar 3: Territorial","Ciudades y comunidades sostenibles","2023"),
("02040401","Nuevas organizaciones de la sociedad","Incluye las políticas públicas orientadas al fortalecimiento de la gobernanza democrática, la modernización de la participación social organizada en la solución de los problemas actuales en los diversos ámbitos de la agenda pública.","J00 Gobierno municipal.","Eje Transversal III: Tecnología y Coordinación para el Buen Gobierno","Alianzas para el desarrollo","2023"),
("02050101","Educación Básica","Engloba las acciones de apoyo tendientes al mejoramiento de los servicios de educación en los diferentes sectores de la población en sus niveles inicial, preescolar, primaria y secundaria conforme a los programas de estudio establecidos en el Plan y programas autorizados por la SEP, asimismo incluye las acciones de apoyo para el fortalecimiento de la formación, actualización, capacitación y profesionalización de docentes y administrativos en concordancia con las necesidades del proceso educativo.","O00 Educación Cultural y Bienestar Social","Pilar 1: Social","Educación Incluyente y de calidad","2023"),
("02050201","Educación Media Superior","Comprende las acciones de apoyo, tendientes a mejorar los servicios de bachillerato general y tecnológico en las modalidades escolarizadas, no escolarizado. mixto, a distancia y abierto, conforme a los programas de estudios establecidos en el plan y programas autorizados por la SEP, con el objeto de fortalecer la formación, actualización, capacitación y profesionalización de docentes y administrativos en concordancia con las necesidades del proceso educativo.","O00 Educación Cultural y Bienestar Social","Pilar 1: Social","Educación Incluyente y de calidad","2023"),
("02050301","Educación Superior","Incluye las acciones de apoyo tendientes a mejorar la atención a la demanda educativa del tipo superior, tecnológica, universitaria, a distancia y formación docente, en las modalidades escolarizada, no escolarizada , abierta, a distancia y mixta, con programas de estudio de calidad basados en competencias profesionales acordes a las necesidades del sector productivo, público y social; fortaleciendo la formación, actualización, capacitación y profesionalización de docentes y administrativos, con la finalidad de formar profesionales con conocimientos científicos, tecnológicos y humanísticos.","O00 Educación Cultural y Bienestar Social","Pilar 1: Social","Educación Incluyente y de calidad","2023"),
("02050501","Educación para adultos","Incluye los proyectos tendientes a incrementar programas que ofrezcan a la población adulta con rezago educativo o desempleo oportunidades para concluir la educación básica, así como capacitarse para incorporarse al mercado laboral.","O00 Educación Cultural y Bienestar Social","Pilar 1: Social","Educación Incluyente y de calidad","2023"),
("02050603","Alimentación para la población infantil","Engloba las acciones encaminadas a disminuir la desnutrición, el sobrepeso y la obesidad en la población preescolar en zonas indígenas, rurales y urbano marginadas del territorio estatal.","","Pilar 1: Social","Alimentación y nutrición para las familias.","2023"),
("02060501","Alimentación y nutrición familiar","Incluye el grupo de proyectos que tienen como propósito procurar elevar el estado nutricional de grupos vulnerables, promover la autosuficiencia alimenticia en zonas y comunidades marginadas, y fomentar hábitos adecuados de consumo.","","Pilar 1: Social","Alimentación y nutrición para las familias","2023"),
("02060701","Pueblos Indígenas","Incluye el quehacer gubernamental para impulsar el desarrollo integral de los pueblos indígenas con la participación social y el respeto a sus costumbres y tradiciones.","","Pilar 1: Social","Desarrollo humano incluyente, sin discriminación y libre de violencia","2023"),
("02060801","Protección a la población infantil y adolescente","Agrupa los proyectos que llevan a cabo los gobiernos municipales para garantizar el respeto a los derechos de los infantes y adolescentes, incluyendo aquellos que se encuentran en condiciones de marginación no acompañada, con acciones que mejoren su bienestar y desarrollo","","Pilar 1: Social","Desarrollo humano incluyente, sin discriminación y libre de violencia","2023"),
("02060802","Atención a Personas con Discapacidad","Integra los proyectos orientados a fortalecer la prevención, rehabilitación e integración social, de las personas con discapacidad, con la participación activa de la población en general, promoviendo el desarrollo de esta población en condiciones de respeto y dignidad.","","Pilar 1: Social","Desarrollo humano incluyente, sin discriminación y libre de violencia","2023"),
("02060803","Apoyo a los adultos mayores","Incluye acciones oportunas y de calidad en materia de nutrición, educación, cultura, recreación, atención psicológica y jurídica, para que los adultos mayores disfruten de un envejecimiento activo, digno y con autosuficiencia.","","Pilar 1: Social","Desarrollo humano incluyente, sin discriminación y libre de violencia","2023"),
("02060804","Desarrollo Integral de la Familia","Engloba los proyectos orientados a fomentar la integración familiar, el respeto y el impulso de valores que permitan a cada individuo un desarrollo armónico, sano, pleno que asista al mejoramiento en las condiciones de vida y empoderando el respeto a los derechos de la niñez, adolescentes, mujeres, discapacitados y adultos mayores.","","Pilar 1: Social","Desarrollo humano incluyente, sin discriminación y libre de violencia","2023"),
("02060805","Igualdad de trato y Oportunidades para la mujer y el hombre","Engloba los proyectos para promover en todos los ámbitos sociales la igualdad sustantiva desde una perspectiva de género como una condición necesaria para el desarrollo integral de la sociedad, en igualdad de condiciones, oportunidades, derechos y obligaciones.","","Eje transversal I: Igualdad de Género","Cultura de igualdad y prevención de la violencia contra las mujeres","2023"),
("02060806","Oportunidades para los jóvenes","Contiene acciones que se orientan a brindar más y mejores oportunidades a los jóvenes que les permitan alcanzar su desarrollo físico – mental adecuado, que les permita incorporarse a la sociedad de manera productiva.","","Pilar 1: Social","Desarrollo humano incluyente, sin discriminación y libre de violencia","2023"),
("03010201","Empleo","Integra los proyectos dirigidos a dinamizar el empleo en territorio municipal, fomentando el desarrollo de la planta productiva, aumentar la oportunidad de empleo, vinculando su oferta y demanda, y garantizar que la población económicamente activa disfrute de las mismas condiciones de empleo, remuneración y oportunidades sin discriminación alguna, mediante la formación de los recursos humanos para el trabajo.","N00 Dirección de Desarrollo Económico","Pilar 2: Económico","Desarrollo económico","2023"),
("03010203","Inclusión económica para la igualdad de genero","Desarrolla acciones enfocadas a disminuir las barreras para la inclusión de las mujeres en la actividad económica del municipio que permitan el pleno ejercicio de sus derechos laborales, fomentando valores de igualdad de género, para construir una relación de respeto e igualdad social.","","Eje transversal I: Igualdad de Género","Cultura de igualdad y prevención de la violencia contra las mujeres","2023"),
("03020101","Desarrollo Agrícola","Agrupa las líneas de acción enfocadas al incremento de los niveles de producción, productividad y rentabilidad de las actividades agrícolas, promoviendo la generación del valor agregado a la producción primaria principalmente de los cultivos intensivos, para satisfacer la demanda interna y reducir las importaciones y minimizar los impactos ambientales que derivan del desarrollo de las diferentes actividades agrícolas.","N01 Desarrollo Agropecuario","Pilar 2: Económico","Desarrollo económico","2023"),
("03020102","Fomento a Productores Rurales","Comprende las acciones tendientes a apoyar la puesta en marcha de proyectos productivos y sociales, fomentar la agroempresa, la capacitación, la organización de productores rurales y la comercialización, a fin de mejorar la productividad y calidad de los productos agropecuarios.","N01 Desarrollo Agropecuario","Pilar 2: Económico","Desarrollo económico","2023"),
("03020103","Fomento Pecuario","Se refiere a las acciones orientadas a incrementar la producción pecuaria y disminuir la dependencia del Estado de México en su conjunto de productos cárnicos y lácteos del mercado nacional e internacional y consolidar agroempresas y organizaciones rentables que propicien el desarrollo integral y sostenible de la actividad pecuaria, generando valor agregado a la producción.","N01 Desarrollo Agropecuario","Pilar 2: Económico","Desarrollo económico","2023"),
("03020104","Sanidad, inocuidad y calidad agroalimentaria","Incorpora el conjunto de líneas de acción que se desarrollan para procurar las condiciones adecuadas en la producción agroalimentaria, así como el desarrollo de acciones de vigilancia para verificar la calidad de los productos.","N01 Desarrollo Agropecuario","Pilar 2: Económico","Desarrollo económico","2023"),
("03020201","Desarrollo forestal.","Comprende los proyectos para asegurar la permanencia de los bosques a través del manejo y aprovechamiento sustentable con la participación directa de dueños, poseedores y prestadores de servicios técnicos, así como acciones dirigidas a evitar la degradación del recurso forestal en el territorio estatal.","G00 Ecología.","Pilar 3: Territorial","Vida de los ecosistemas terrestres","2023"),
("03020301","Fomento acuícola","Integra acciones encaminadas a la implementación de proyectos productivos para desarrollar la producción acuícola del municipio, de acuerdo al potencial productivo regional, impulsando su aprovechamiento sustentable, para contribuir a la generación de empleos productivos y a la mejora de la dieta básica de la población.","N00 Dirección de Desarrollo Económico","Pilar 2: Económico","Desarrollo económico","2023"),
("03020601","Seguros y garantías financieras agropecuarias.","Integra las acciones realizadas en el marco de un sistema de riesgos; que evite la descapitalización del productor ante la ocurrencia de un siniestro y dé protección al patrimonio de la gente del campo a través de seguros y fianzas.","N00 Dirección de Desarrollo Económico.","Pilar 2: Económico","Desarrollo económico","2023"),
("03030501","Electrificación","Incorpora los proyectos que fomentan el uso de tecnologías que mejoren la calidad de los servicios de electrificación, así como la habitabilidad, seguridad e higiene de la vivienda social, para hacerla económica y ambientalmente sustentable, privilegiando en la atención de las comunidades que carecen del servicio.","H00 Servicios Públicos","Pilar 3: Territorial","Energía asequible y no contaminante","2023"),
("03040201","Modernización Industrial y del Comercio","Agrupa los proyectos orientados a fomentar una cultura empresarial que asegure el desarrollo del comercio y la modernización industrial para proyectar y atraer inversión productiva extranjera, nacional y local, con fuerte impulso de las exportaciones, donde las cadenas productivas concreten el fortalecimiento de las micro, pequeñas, medianas y grandes empresas con absoluto apego a la normatividad aplicable y respeto al medio ambiente.","N00 Dirección de Desarrollo Económico","Pilar 2: Económico","Desarrollo económico","2023"),
("03050101","Modernización de la movilidad y el transporte terrestre.","Comprende las acciones orientadas a la modernización y optimización del servicio de transporte terrestre, a través de la coordinación intergubernamental para la organización técnica oportuna y racional que contribuya a la eficiencia y calidad en la prestación del servicio de transporte público.","J00 Gobierno Municipal.","Pilar 3: Territorial","Ciudades y comunidades sostenibles","2023"),
("03050103","Modernización de la infraestructura para el transporte terrestre.","Incluye acciones para ampliar, mantener y mejorar las condiciones de la red carretera en el territorio estatal y fomentar el equipamiento, con el propósito de que contribuyan al mejoramiento de la movilidad, el desarrollo regional, metropolitano y suburbano.","F00 Desarrollo urbano y obras públicas.","Pilar 3: Territorial","Ciudades y comunidades sostenibles","2023"),
("03070101","Fomento Turístico","Integra los proyectos que lleva a cabo el Gobierno Municipal para impulsar la inversión social y privada que incremente la calidad de los servicios turísticos, desarrollando nuevos destinos altamente competitivos que generen ingresos y empleo para la población, a través de acciones tendientes a mejorar, diversificar y consolidar la oferta de infraestructura turística con una regulación administrativa concertada con los prestadores de servicios.","N00 Dirección de Desarrollo Económico","Pilar 2: Económico","Desarrollo económico","2023"),
("03080101","Investigación científica.","Comprende las líneas de acción destinadas al apoyo de la investigación científica básica y aplicada, promoviendo el desarrollo del conocimiento científico.","N00 Dirección de Desarrollo Económico.","Pilar 2: Económico","Innovación, investigación y desarrollo","2023"),
("03090301","Promoción artesanal","Agrupa los proyectos destinados a preservar y fomentar las expresiones artesanales municipales e impulsar su creatividad, diseño, producción y comercialización en el mercado local, nacional e internacional, a fin de mejorar el nivel de vida de los artesanos y grupos étnicos.","N00 Dirección de Desarrollo Económico.","Pilar 2: Económico","Desarrollo económico","2023"),
("04010101","Deuda Pública","Refiere todas aquellas acciones relacionadas con el manejo eficiente y sustentable de la deuda pública, entre ellas, la contratación, amortización, ser vicio, refinanciamiento y/o reestructuración de la deuda del gobierno municipal, así como el registro, vigilancia, seguimiento y control de sus obligaciones multianuales correspondientes al gobierno municipal.","L00 Tesorería","Eje transversal II: Gobierno Moderno, Capaz y Responsable","Finanzas públicas sanas","2023"),
("04020101","Transferencias","Comprende la suma de recursos transferidos por los municipios y organismos municipales para cumplir con objetivos diversos además engloba las acciones necesarias para la celebración de convenios previstos en la Ley de Coordinación Fiscal vigente.","L00 Tesorería","Eje transversal II: Gobierno Moderno, Capaz y Responsable","Finanzas públicas sanas","2023"),
("04040101","Previsiones para el pago de adeudos de ejercicios fiscales anteriores.","Tiene por objeto integrar los recursos financieros presupuestales para el pago de adeudos que no fueron cubiertos en ejercicios anteriores","L00 Tesorería Pilar o Eje transversal: Eje transversal II: Gobierno Moderno, Capaz y Responsable","Eje transversal II: Gobierno Moderno, Capaz y Responsable","Finanzas públicas sanas","2023");
INSERT INTO programas_presupuestarios(codigo_programa, nombre_programa, objetivo_pp, dependencia_general_gaceta, pilar_o_eje, tema_desarrollo, anio) VALUES 
("01020401","Derechos Humanos.","Engloba los proyectos orientados a proteger, defender y garantizar los derechos humanos de todas las personas que se encuentren en cada territorio municipal, sin discriminación por condición alguna y fomentar la cultura de los derechos humanos para promover el respeto y la tolerancia entre los individuos en todos los ámbitos de la interrelación social apoyando a las organizaciones sociales que impulsan estas actividades.","A02 Derechos Humanos.","Pilar 4 Seguridad","Derechos Humanos","2024"),
("01030101","Conducción de las políticas generales de gobierno.","Incluye las acciones que favorezcan el desarrollo de un gobierno democrático que impulse la participación social y ofrezca servicio de calidad en el marco de legalidad y justicia, para elevar las condiciones de vida de la población.","J00 Gobierno Municipal.","Eje transversal II Gobierno Moderno, Capaz y Responsable","Comunicación y diálogo con la ciudadanía como elemento clave de gobernabilidad.","2024"),
("01030201","Democracia y pluralidad política.","Incorpora las acciones orientadas a la realización de acciones de apoyo al estado democrático con la participación ciudadana y la consolidación del estado de derecho y la justicia social, propiciando una cultura política y fortaleciendo sistema de partidos.","J00 Gobierno Municipal.","Eje transversal II Gobierno Moderno, Capaz y Responsable","Comunicación y diálogo con la ciudadanía como elemento clave de gobernabilidad.","2024"),
("01030301","Conservación del patrimonio público.","Incluye las acciones encaminadas a mantener y transmitir el conocimiento del patrimonio público tangible e intangible, como devenir de la identidad de los mexiquenses.","J00 Gobierno Municipal.","Pilar 3 Territorial","Ciudades y comunidades sostenibles.","2024"),
("01030401","Desarrollo de la función pública y ética en el servicio público.","Considera las acciones orientadas a la mejora en la prestación de los servicios que recibe la población de manera clara, honesta, pronta y expedita, promoviendo que los servidores públicos realicen su función con calidez, y cuenten con las competencias y conducta ética necesarias en el servicio público, conforme a los principios que rigen la actuación del servidor público.","K00 Contraloría","Eje transversal II Gobierno Moderno, Capaz y Responsable","Eficiencia y eficacia en el sector público","2024"),
("01030402","Sistema Anticorrupción del Estado de México y Municipios.","Conjunto de acciones orientadas a establecer las bases de coordinación entre el Estado y los Municipios para el funcionamiento de los Sistemas Anticorrupción, de conformidad con lo dispuesto en la Constitución Política de los Estados Unidos Mexicanos, la Constitución Política del Estado Libre y Soberano de México, la Ley General del Sistema Nacional Anticorrupción y la Ley del Sistema Anticorrupción del Estado de México y Municipios, para que las autoridades estatales y municipales competentes prevengan, investiguen y sancionen las faltas administrativas y los hechos de corrupción.","","Eje transversal II: Gobierno Moderno, Capaz y Responsable","Sistema Anticorrupción del Estado de México y Municipios","2024"),
("01030501","Asistencia Jurídica al Ejecutivo.","Comprende todas las acciones orientadas al fortalecimiento y mejora de los procedimientos regulatorios y conductos legales establecidos, que influyan directamente en la garantía jurídica del gobierno y la sociedad.","M00 Consejería Jurídica.","Eje transversal II Gobierno Moderno, Capaz y Responsable","Eficiencia y eficacia en el sector público","2024"),
("01030801","Política territorial.","Es el conjunto de acciones a aplicar en una demarcación territorial definida, en beneficio de toda la población o comunidades específicas ahí establecidas.","J00 Gobierno Municipal.","Pilar 3 Territorial","Ciudades y comunidades sostenibles","2024"),
("01030902","Reglamentación Municipal.","Se orienta al cumplimiento de las atribuciones contenidas en el Título VI de la Ley Orgánica Municipal del Estado de México, vigente.","D00 Secretaría del Ayuntamiento","Eje transversal II Gobierno Moderno, Capaz y Responsable","Estructura del gobierno municipal","2024"),
("01030903","Mediación y conciliación municipal.","Se orienta al cumplimiento de las atribuciones contenidas en el Título V de la Ley Orgánica Municipal del Estado de México, vigente.","M00 Consejería jurídica.","Pilar 4 Seguridad","Mediación y conciliación","2024"),
("01030904","Coordinación intergubernamental regional.","Se orienta al impulso del desarrollo armónico sustentable de las regiones con la eficaz intervención y coordinación de un ayuntamiento con otros gobiernos municipales, estatales y el gobierno federal en beneficio de la población y sus actividades productivas; así mismo, incluye las acciones que permitan fortalecer la cooperación regional, incluyendo el desarrollo metropolitano, para alcanzar una efectiva coordinación y aplicación de políticas públicas de ámbito regional.","N00 Dirección de Desarrollo Económico","Eje transversal II Gobierno Moderno, Capaz y Responsable","Coordinación Institucional","2024"),
