function comparador($cero1a, $excel)
{
    foreach ($cero1a as $o) {
        $gen = $o[0];
        $aux = $o[1];
        $proy = $o[2] . $o[3] . $o[4] . $o[5] . $o[6] . $o[7];
        $clave_o = $gen . $aux . $proy;

        $find = 0;

        foreach ($excel as $e) {
            $gen_e = $e[0];
            $aux_e = $e[1];
            $proy_e = $e[2];
            $clave_e = $gen_e . $aux_e . $proy_e;

            if ($clave_o == $clave_e) {
                $find = $e;
            }
        }
        if ($find != 0) {
            print $find[0] . "|" . $find[1] . "|" . $find[2] . "|" . $find[3] . '<br>';
        } else {
            print $gen . "|" . $aux . "|" . $proy . '|<br>';
        }
    }
}



