// El primer paso sera comparar los indicadores del TXT con los indicadores de la base de datos indicadores para agregarlos a indicadores uso
function comparaIndicadores($con, $indicadores_01d)
{
    print "INSERT INTO indicadores_uso (<br>id_indicador_gaceta, anio, id_dep_general, id_dep_aux, id_proyecto, tipo_op_a, tipo_op_b, umedida_a, umedida_b, interpretacion, tipo, factor_de_comparacion, desc_factor_de_comparacion, linea_base, at1, at2, at3, at4, bt1, bt2, bt3, bt4, id_dependencia, id_area) VALUES <br>";
    $stm = $con->query("SELECT * FROM indicadores
    WHERE anio = '2024'");
    $indicadores = $stm->fetchAll(PDO::FETCH_ASSOC);

    $stm = $con->query("SELECT * FROM dependencias_generales WHERE tipo = 'Municipios' AND anio ='2024'");
    $dependencias_generales = $stm->fetchAll(PDO::FETCH_ASSOC);
    $stm = $con->query("SELECT * FROM dependencias_auxiliares WHERE tipo = 'Municipios' AND anio ='2024'");
    $dependencias_auxiliares = $stm->fetchAll(PDO::FETCH_ASSOC);
    $stm = $con->query("SELECT * FROM proyectos WHERE anio ='2024'");
    $proyectos = $stm->fetchAll(PDO::FETCH_ASSOC);

    $stm = $con->query("SELECT *, ar.id_dependencia as id_dependencia FROM areas ar
    LEFT JOIN dependencias_generales dg ON dg.id_dependencia = ar.id_dependencia_general
    LEFT JOIN dependencias_auxiliares da ON da.id_dependencia_auxiliar = ar.id_dependencia_aux
    LEFT JOIN proyectos py ON py.id_proyecto = ar.id_proyecto 
    WHERE ar.anio ='2024'");
    $areas = $stm->fetchAll(PDO::FETCH_ASSOC);



    $r = 1;
    $arry = array();
    foreach ($indicadores_01d as $i) {
        if ($r == 1) {
            $arry[] = $i;
            $r = 2;
        } elseif ($r == 2) {
            $arry[] = $i;

            $nombre_ante = mb_strtolower($arry[1][12]);
            $nombre_ante = rtrim($nombre_ante, '.');

            $gen = $arry[0][3];
            $aux = $arry[0][4];
            $proy = $arry[0][5] . $arry[0][6] . $arry[0][7] . $arry[0][8] . $arry[0][9] . $arry[0][10];
            $gen_c = 0;
            $aux_c = 0;
            $proy_c = 0;
            $id_area = 0;

            $find = 0;

            foreach ($areas as $area) {
                $cve_area = $area['clave_dependencia'] . $area['clave_dependencia_auxiliar'] .  $area['codigo_proyecto'];
                $cve_ind = $gen . $aux . $proy;
                if ($cve_area == $cve_ind) {
                    $id_area = $area['id_area'];
                    $id_dependencia = $area['id_dependencia'];
                }
            }
            foreach ($dependencias_generales as $d) {
                if ($d['clave_dependencia'] == $gen) {
                    $gen_c = $d['id_dependencia'];
                }
            }

            foreach ($dependencias_auxiliares as $da) {
                if ($da['clave_dependencia_auxiliar'] == $aux) {
                    $aux_c = $da['id_dependencia_auxiliar'];
                }
            }

            foreach ($proyectos as $p) {
                if ($p['codigo_proyecto'] == $proy) {
                    $proy_c = $p['id_proyecto'];
                }
            }

            foreach ($indicadores as $in) {
                $nombre_indicador = mb_strtolower($in['nombre']);
                $nombre_indicador = rtrim($nombre_indicador, '.');

                similar_text($nombre_ante, $nombre_indicador, $percent_nombre);

                if ($percent_nombre > 90) {
                    $find = $in;
                    break; // Rompe el bucle si encuentra una coincidencia
                }
            }




            if ($find == 0 || $gen_c == 0 || $aux_c == 0 || $proy_c == 0 || $id_area == 0) {
                print "error";
                die();
            }
            if ($find != 0) {
                print "(" .
                    $find['id_indicador'] . ',"2024",' .
                    $gen_c . ',' .
                    $aux_c . ',' .
                    $proy_c . ',"' .
                    $arry[0][23] . '","' . // operacion a
                    $arry[1][23] . '","' . // operacion b
                    $arry[0][22] . '","' . // u medida a
                    $arry[1][22] . '","' . // u medida b
                    $arry[0][15] . '","' . // interpretacion
                    $arry[0][13] . '","' . // tipo
                    $arry[0][17] . '","' . // factor_de_comparacion
                    $arry[0][18] . '","' . // Desc fac comp
                    $arry[0][19] . '","' . // Linea Base
                    $arry[0][24] . '","' . // 1t A
                    $arry[0][25] . '","' . // 2t A
                    $arry[0][26] . '","' . // 3t A
                    $arry[0][27] . '","' . // 4t A
                    $arry[1][24] . '","' . // 1t B
                    $arry[1][25] . '","' . // 2t B
                    $arry[1][26] . '","' . // 3t B
                    $arry[1][27] . '",' . // 4t B
                    $id_dependencia . ',' . 
                    $id_area . '),' . 

                    "<br>";
            }
            $arry = array();
            $r = 1;
        }
    }
}


comparaIndicadores($con, $indicadores_01d);
